sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var ga=new GlideAjax('AutoPopulateOpportunityField');
	ga.addParam('sysparm_name','populateField');
	ga.addParam('sysID',newValue);
	ga.getXML(callback);
	function callback(response){
		var answer=response.responseXML.documentElement.getAttribute('answer');
		// alert(answer);

		var obj=JSON.parse(answer);
		// alert(typeof(obj));
		g_form.setValue('client_name',obj.client_name);
		g_form.setValue('matter_name',obj.matter_name);
		g_form.setValue('client_type',obj.client_type);
		g_form.setValue('matter_type',obj.matter_type);
		g_form.setValue('representation_type',obj.representation_type);
		g_form.setValue('referral_source',obj.referral_source);
		g_form.setValue('matter_description',obj.matter_description);
		g_form.setValue('matter_office',obj.matter_office);
		g_form.setValue('deadline',obj.deadline);
		g_form.setValue('keni_request_submission_date',obj.keni_request_submission_date);
		g_form.setValue('opportunity_created',obj.opportunity_created);
		g_form.setValue('language_requirement',obj.language_requirement);
	}
}
