sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading && newValue === '') {
        g_form.setSectionDisplay('immigration', false);
        return;
    }

    var matter = g_form.getValue('matter_type');
    var ga = new GlideAjax('Matter_type_is_immigration');
    ga.addParam('sysparm_name', 'getFields');
    ga.addParam('sysparm_id', matter);
    ga.getXML(response);

    function response(res) {
        var answer = res.responseXML.documentElement.getAttribute('answer');
        // alert(answer);
        if (answer == 'true') {
            g_form.setSectionDisplay('immigration', true);
            make_field_mandatory(true);
        } else {
			clearFieldValues();
            make_field_mandatory(false);

            g_form.setSectionDisplay('immigration', false);

        }
    }

    var fieldsName = ['a_number', 'responsible_office', 'country_of_origin', 'other_deadline', 'eoir_filing_deadline', 'other_deadline_description', 'eoir_filing_description',
        'priority_date', 'uscis_filing_deadline', 'hearing_date', 'uscis_filing_description', 'hearing_type',
        'ice_deadline_ck_in', 'hearing_location', 'ice_deadline_ck_in_description', 'action_needed', 'case_status', 'date_of_last_update'
    ];

    function make_field_mandatory(boolean) {

            for (var i = 0; i < fieldsName.length; i++) {
                g_form.setMandatory(fieldsName[i], boolean);
            }

    }
	function clearFieldValues(){
		for (var i = 0; i < fieldsName.length; i++) {
                g_form.clearValue(fieldsName[i]);
            }
	}

}
