sys_script_include.script:
var Matter_type_is_immigration = Class.create();
Matter_type_is_immigration.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getFields: function() {
        var sysId = this.getParameter('sysparm_id').split(',');
        for (var i = 0; i < sysId.length; i++) {
            var gr = new GlideRecord('x_1263211_pro_bono_foundation_attribute_data');
            gr.get(sysId[i].toString());
            var value = gr.getValue('value');
            var foundImmigration = value.toLowerCase();
            if (foundImmigration.includes('immigration')) {
                return 'true';
            }
        }
		return 'false';

    },
    type: 'Matter_type_is_immigration'
});
