sys_script_include.script:
var one_to_one_relationship_with_user = Class.create();
one_to_one_relationship_with_user.prototype = {
    initialize: function() {},
    one_to_one: function() {
        var array = [];
        var timekeeper_array = [];
        var timekeeper = new GlideRecord('x_1263211_pro_bono_timekeeper_register');
        timekeeper.query();
        while (timekeeper.next()) {
			if(timekeeper.getValue('user')!=''){
				timekeeper_array.push(timekeeper.getValue('user'));
			}

        }
        var gr = new GlideRecord('sys_user');
        gr.addActiveQuery();
        gr.query();
        while (gr.next()) {

            if (!timekeeper_array.includes(gr.getValue('sys_id'))) {
                array.push(gr.getValue('sys_id'));
            }
        }
        return 'sys_idIN' + array;
    },

    type: 'one_to_one_relationship_with_user'
};
