sys_script_include.script:
var AutoPopulateOpportunityField = Class.create();
AutoPopulateOpportunityField.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	populateField:function(){
		var sysId=this.getParameter('sysID');
		var gr=new GlideRecord('x_1263211_pro_bono_opportunity');
		gr.addQuery('sys_id',sysId);
		gr.query();
		if(gr.next()){
			var obj={};
			obj.client_name=gr.getDisplayValue('client_name');
			obj.matter_name=gr.getDisplayValue('matter_name');
			obj.representation_type=gr.getValue('representation_type');
			obj.client_type=gr.getValue('client_type');
			obj.matter_type=gr.getValue('matter_type');
			obj.referral_source=gr.getValue('referral_source');
			obj.matter_description=gr.getDisplayValue('matter_description');
			obj.matter_office=gr.getDisplayValue('matter_office');
			obj.deadline=gr.getDisplayValue('deadline');
			obj.keni_request_submission_date=gr.getDisplayValue('keni_request_submission_date');
			obj.opportunity_created=gr.getDisplayValue('opportunity_created');
			obj.language_requirement=gr.getValue('language_requirement');
			return JSON.stringify(obj);
		}

	},
    type: 'AutoPopulateOpportunityField'
});
